bestStreak=999
currentStreak=123
curDay=0
activities=[0]*7


def getBatteryPct():
    bus_voltage = battery.getBusVoltage_V()
    pct = (bus_voltage -3)/1.2*100
    if(pct<0):pct=0
    elif(pct>100):pct=100
    if(pct==100):pct=99 #to fit the screen
    return(pct)


def loadTemplateScreen(bottomPressed=False,topPressed=False):
    screen.fill(0x0000)
    if bottomPressed and topPressed:
        data=template_bothbutton
    elif bottomPressed:
        data=template_bottombutton
    elif topPressed:
        data=template_topbutton
    else:
        data=template_nobutton
    fbuf = framebuf.FrameBuffer(data,128,64,framebuf.MONO_HMSB)
    screen.blit(fbuf, 0, 0)
    screen.text("YogaExrRunHike",1,35,screen.white)

def loadBateryPct():
    screen.text(str(int(getBatteryPct())),95,51,screen.white)

def loadStreaks():
    screen.text(str(data["bestStreak"]),17,51,screen.white)
    screen.text(str(data["currentStreak"]),59,51,screen.white)

def drawSelector():
    if time.time()%2==0:
        x=6+(data["curDay"]*15)
        screen.fill_rect(x,17,10,10,screen.white)

def drawActivity():
    c=0
    for day in data["activities"]:
        x=6+1+(c*15)
        screen.text(activitiesLookup[data["activities"][c]],x,19,screen.white)
        c+=1

def saveData():
    print(3)
    with open("data.json", "w") as f:
        print(4)
        print(data)
        json.dump(data, f)
        print(5)
    print(6)
    
def refreshScreen():
    global threadInUse
    print(777)
    threadInUse=True
    loadTemplateScreen(keyA.value()==0,keyB.value()==0)
    loadBateryPct()
    loadStreaks()
    drawActivity()
    drawSelector()
    screen.show()
    #saveData()
    threadInUse=False

def refresh():
    global prevKeyA
    global prevKeyB
    global data
    keyAPressed=keyA.value()==0
    keyBPressed=keyB.value()==0
    
    if keyBPressed and not prevKeyB:
        data["curDay"]=(data["curDay"]+1)%7
    if keyAPressed and not prevKeyA:
        data["activities"][data["curDay"]]=(data["activities"][data["curDay"]]+1)%5
    if not threadInUse:
        time.sleep(0.01)
        refreshScreen()
        #_thread.start_new_thread(refreshScreen, ())   
    
    print(1)
    saveData()
    print(2)
    time.sleep(1.2)
    
while True:
    refresh()